Ext.define('HEART.view.EMITrackMeditation', {
	extend: 'Ext.form.Panel',

	config: {
		items: [
			{
				xtype: 'fieldset',
				title: '<center>Did you meditate today?<center>',
				items: [
					{
						xtype: 'button',
						itemId: 'not',
						text: 'Not today'
					},
					{
						xtype: 'label',
						html: 'Yes. <br/><br/> How many minutes?',
						itemId: 'describe'
					},
					{
						xtype: 'textfield',
						goola: 'number',
						itemId: 'plus',
						label: 'min',
						labelAlign: 'right',
						name: 'time',
						required: true
					},
					{
						xtype: 'button',
						action: 'exercise',
						itemId: 'done',
						text: 'Done'
					}
				]
			}
		],
		listeners: [
			{
				fn: 'onNotTap',
				event: 'tap',
				delegate: '#not'
			},
			{
				fn: 'onMyTimeSave',
				event: 'tap',
				delegate: '#done'
			}
		]
	},

	onNotTap: function(button, e, eOpts) {
		meditation = HEART.getItem('local', 'meditation');

		if(meditation==null){
			meditation = 0;
		}

		Ext.Msg.alert(':)', "<div align='center'>["+ meditation +"]min meditated so far.</div>", Ext.emptyFn);
	},

	onMyTimeSave: function(button, e, eOpts) {
		meditation = HEART.getItem('local', 'meditation');
		plus = button.parent.child('textfield').getValue();

		if(meditation==null||isNaN(meditation)){
			meditation=0;
		}

		meditation = parseInt(meditation);
		plus = parseInt(plus);
		meditation += plus;

		HEART.setItem('local', 'meditation', meditation);
	}

});