Ext.define('HEART.view.EMITrackMeditation', {
	extend: 'Ext.form.Panel',

	config: {
		items: [
			{
				xtype: 'fieldset',
				itemId: 'fieldSetMeditate',
				title: 'Did you meditate today?',
				items: [
					{
						xtype: 'button',
						action: 'exercise',
						itemId: 'not',
						ui: 'decline',
						text: 'Not today'
					},
					{
						xtype: 'button',
						itemId: 'yes_meditate',
						ui: 'action',
						text: 'Yes'
					}
				]
			},
			{
				xtype: 'fieldset',
				hidden: true,
				itemId: 'fieldSetMinutes',
				title: 'How many minutes <br/> did you meditate?',
				items: [
					{
						xtype: 'numberfield',
						itemId: 'plus',
						label: 'min',
						labelAlign: 'right',
						name: 'time',
						required: true,
						minValue: 0
					},
					{
						xtype: 'button',
						action: 'exercise',
						itemId: 'done',
						text: 'Done'
					}
				]
			},
			{
				xtype: 'label',
				html: '<div align=\'center\'>X min meditated so far </div>',
				itemId: 'lblMins'
			}
		],
		listeners: [
			{
				fn: 'onNotTap',
				event: 'tap',
				delegate: '#not'
			},
			{
				fn: 'onYes_meditateTap',
				event: 'tap',
				delegate: '#yes_meditate'
			},
			{
				fn: 'onMyTimeSave',
				event: 'tap',
				delegate: '#done'
			}
		]
	},

	onNotTap: function(button, e, eOpts) {

	},

	onYes_meditateTap: function(button, e, eOpts) {

		var meditate = button.parent;
		var minutes = button.parent.parent.child('#fieldSetMinutes');
		minutes.child("#plus").setValue('');
		minutes.setShowAnimation({type: "fadeIn", duration: 1024});
		minutes.show();
		meditate.hide();
	},

	onMyTimeSave: function(button, e, eOpts) {
		meditation = HEART.getItem('local', 'meditation');
		plus = button.parent.child('textfield').getValue();

		if(meditation===null||isNaN(meditation)){
			meditation=0;
		}

		meditation = parseInt(meditation);
		plus = parseInt(plus);
		meditation += plus;

		HEART.setItem('local', 'meditation', meditation);
	},

	initialize: function() {
		meditation = HEART.getItem('local', 'meditation');

		if(meditation===null || isNaN(meditation)){
			meditation = 0;
		}
		var lbl = this.child("#lblMins");
		lbl.setHtml( "<div align='center'>"+ meditation +"min meditated so far.</div>");

		var plus = this.child("#fieldSetMinutes").child("#plus");
		plus.setValue(0);
		this.callParent();
	}

});