Ext.define('HEART.controller.ExerciseController', {
	extend: 'Ext.app.Controller',

	config: {
		refs: {
			exercises: '#exercises',
			stressed: '#stressed'
		},

		control: {
			"button[action=exercise]": {
				tap: 'onButtonTap'
			}
		}
	},

	onButtonTap: function(button, e, eOpts) {
		var form = button.parent.parent;
		var values = form.getValues();
		var content = values;

		console.log(form.record_data);

		var type = form.record_data.get('type');

		content.type = type;
		content.user = 'kyle';

		var username = '2551135934';
		var password = '4bbdbcd0b64e75ca047aac5e7d44934b';
		var base = Base64.encode(username + ':' + password);

		var auth = "Basic " + base;

		Ext.Ajax.request({
			method: 'POST',
			withCredentials: true,
			useDefaultXhrHeader: false,

			url: 'http://sensocol.bj.brainpage.com/v1/sensors/channels',
			headers: {Authorization: auth, 'Access-Control-Allow-Origin': '*'},

			params: content,

			success: function(response) {
				console.log(response);
			},

			failure: function(response) {
				var feeds = Ext.getStore("Feeds");
				feeds.add({timestamp: Date.now(), type: type, data: values, mark: false});
				feeds.sort("timestamp", 'DESC');
				feeds.sync();
			}
		});

		this.getExercises().pop();
	}

});